import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

/* ===========================================================
   COMPONENTE PRINCIPAL: App
   ===========================================================
   Este componente gerencia toda a aplicação ECC:
   - Cadastro de casais
   - Listagem de casais
   - Atualização de casais
   - Exclusão de casais
   - Aniversariantes do dia
   =========================================================== */
function App() {
  /* ===========================================================
     ESTADOS PRINCIPAIS
     ===========================================================
     casais: lista de todos os casais cadastrados
     telaAtual: controla qual tela está sendo exibida
     form: dados do formulário de cadastro
     sucesso: mensagens de sucesso ou erro
     aniversariantes: lista de aniversariantes do dia
     casalSelecionado: casal que será atualizado
     =========================================================== */
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios, atualiza
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);
  const [casalSelecionado, setCasalSelecionado] = useState(null);

  /* ===========================================================
     FUNÇÕES PARA CARREGAR DADOS
     =========================================================== */
  const loadCasais = async () => {
    try {
      const res = await axios.get("http://localhost:8000/casais");
      setCasais(res.data);
    } catch (err) {
      console.error("Erro ao carregar casais:", err);
    }
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  // Carrega os casais ao iniciar o app
  useEffect(() => {
    loadCasais();
  }, []);

  /* ===========================================================
     FUNÇÃO DE CADASTRO DE CASAL
     =========================================================== */
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form);

      // limpa formulário
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });

      loadCasais();
      setSucesso("✅ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("⚠️ Este casal já está cadastrado!");
      } else {
        setSucesso("❌ Erro ao cadastrar casal. Tente novamente.");
      }
      setTimeout(() => setSucesso(""), 4000);
    }
  };

  /* ===========================================================
     COMPONENTE INTERNO: AtualizaCasal
     ===========================================================
     Responsável por atualizar os dados de um casal existente
     Recebe o casal a ser atualizado e função para voltar à lista
     =========================================================== */
  function AtualizaCasal({ casal, voltar }) {
    const [formAtualiza, setFormAtualiza] = useState({
      nome_esposo: casal.nome_esposo || "",
      nome_esposa: casal.nome_esposa || "",
      celular_esposo: casal.celular_esposo || "",
      celular_esposa: casal.celular_esposa || "",
      endereco: casal.endereco || "",
      bairro: casal.bairro || "",
      funcao: casal.funcao_no_ecc || "",
      aniversario: casal.aniversario_esposo || "",
    });

    // Atualiza o estado do formulário conforme digitação
    const handleChange = (e) => {
      setFormAtualiza({ ...formAtualiza, [e.target.name]: e.target.value });
    };

    // Salva atualização no backend
    const handleSubmitAtualiza = async (e) => {
      e.preventDefault();
      try {
        await axios.put(
          `http://localhost:8000/casais/${casal.id}`,
          formAtualiza
        );
        alert("Casal atualizado com sucesso!");
        loadCasais();
        voltar(); // volta para a tela de lista
      } catch (err) {
        console.error(err);
        alert("Erro ao atualizar casal!");
      }
    };

    return (
      <div className="atualiza-container">
        <h2>Atualizar Casal</h2>
        <form onSubmit={handleSubmitAtualiza}>
          <input
            name="nome_esposo"
            value={formAtualiza.nome_esposo}
            onChange={handleChange}
            placeholder="Nome do Esposo"
          />
          <input
            name="nome_esposa"
            value={formAtualiza.nome_esposa}
            onChange={handleChange}
            placeholder="Nome da Esposa"
          />
          <input
            name="celular_esposo"
            value={formAtualiza.celular_esposo}
            onChange={handleChange}
            placeholder="Celular do Esposo"
          />
          <input
            name="celular_esposa"
            value={formAtualiza.celular_esposa}
            onChange={handleChange}
            placeholder="Celular da Esposa"
          />
          <input
            name="endereco"
            value={formAtualiza.endereco}
            onChange={handleChange}
            placeholder="Endereço"
          />
          <input
            name="bairro"
            value={formAtualiza.bairro}
            onChange={handleChange}
            placeholder="Bairro"
          />
          <input
            name="funcao"
            value={formAtualiza.funcao}
            onChange={handleChange}
            placeholder="Função já exercida"
          />
          <input
            type="date"
            name="aniversario"
            value={formAtualiza.aniversario}
            onChange={handleChange}
          />
          <button type="submit">Atualizar</button>
        </form>
        <button onClick={voltar}>⬅ Voltar</button>
      </div>
    );
  }

  /* ===========================================================
     LABELS PARA O FORMULÁRIO
     =========================================================== */
  const labels = {
    nome_esposo: "NOME ESPOSO",
    nome_esposa: "NOME ESPOSA",
    celular_esposo: "CELULAR ESPOSO",
    celular_esposa: "CELULAR ESPOSA",
    aniversario_esposo: "ANIVERSÁRIO ESPOSO",
    aniversario_esposa: "ANIVERSÁRIO ESPOSA",
    endereco: "ENDEREÇO",
    bairro: "BAIRRO",
    funcao_no_ecc: "FUNÇÃO NO ECC",
  };

  /* ===========================================================
     RENDERIZAÇÃO DO COMPONENTE PRINCIPAL
     =========================================================== */
  return (
    <div className="container">
      <h1>CADASTRO DE CASAIS</h1>

      {/* ---------------- MENU PRINCIPAL ---------------- */}
      {telaAtual === "menu" && (
        <div className="button-container">
          <button onClick={() => setTelaAtual("cadastro")}>
            CADASTRAR CASAL
          </button>
          <button onClick={() => setTelaAtual("lista")}>BUSCAR CASAL</button>
          <button
            onClick={() => {
              setTelaAtual("aniversariantes");
              loadAniversariantes();
            }}
          >
            ANIVERSARIANTES DO DIA
          </button>
          <button onClick={() => setTelaAtual("relatorios")}>EXCLUIR</button>
        </div>
      )}

      {/* ---------------- FORMULÁRIO DE CADASTRO ---------------- */}
      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{labels[field]}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">SALVAR CASAL</button>
          {sucesso && <p className="msg-sucesso">{sucesso}</p>}
        </form>
      )}

      {/* ---------------- LISTA DE CASAIS ---------------- */}
      {telaAtual === "lista" && (
        <>
          <h2>CASAIS CADASTRADOS</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa}
                </p>
                <p>
                  <strong>Endereço:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>Função no ECC:</strong> {c.funcao_no_ecc}
                </p>

                {/* Botão de atualizar casal */}
                <button
                  onClick={() => {
                    setCasalSelecionado(c);
                    setTelaAtual("atualiza");
                  }}
                  className="btn-atualizar"
                >
                  Atualizar
                </button>
              </li>
            ))}
          </ul>
        </>
      )}

      {/* ---------------- TELA DE ATUALIZAÇÃO ---------------- */}
      {telaAtual === "atualiza" && casalSelecionado && (
        <AtualizaCasal
          casal={casalSelecionado}
          voltar={() => setTelaAtual("lista")}
        />
      )}

      {/* ---------------- ANIVERSARIANTES ---------------- */}
      {telaAtual === "aniversariantes" && (
        <div className="aniversariantes-container">
          <h2>🎉 PARABÉNS! 🎂</h2>
          {aniversariantes.length > 0 ? (
            aniversariantes.map((p) => (
              <div key={p.id} className="aniversariante-card">
                <p>
                  🎂 <strong>{p.nome}</strong> faz aniversário hoje!
                </p>
                <p className="versiculo">
                  📖 "O amor é paciente, o amor é bondoso..." <br /> (1
                  Coríntios 13:4)
                </p>
              </div>
            ))
          ) : (
            <p>Hoje não há aniversariantes.</p>
          )}
          <button onClick={() => setTelaAtual("menu")}>⬅ Voltar</button>
        </div>
      )}

      {/* ---------------- EXCLUSÃO ---------------- */}
      {telaAtual === "relatorios" && (
        <>
          <h2>EXCLUIR CASAL</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal excluído com sucesso!");
                    }
                  }}
                  className="btn-excluir"
                >
                  EXCLUIR
                </button>
              </li>
            ))}
          </ul>
          <button onClick={() => setTelaAtual("menu")}>⬅ Voltar</button>
        </>
      )}
    </div>
  );
}

export default App;
