import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  // -------------------
  // 1ï¸âƒ£ Estados do App
  // -------------------
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState({ tela: "menu", id: null });
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  // -------------------
  // 2ï¸âƒ£ FunÃ§Ãµes de API
  // -------------------
  const loadCasais = async () => {
    try {
      const res = await axios.get("http://localhost:8000/casais");
      setCasais(res.data);
    } catch (error) {
      console.error("Erro ao carregar casais:", error);
    }
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form);
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });
      loadCasais();
      setSucesso("âœ… Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("âš ï¸ Este casal jÃ¡ estÃ¡ cadastrado!");
        setForm({
          nome_esposo: "",
          nome_esposa: "",
          celular_esposo: "",
          celular_esposa: "",
          aniversario_esposo: "",
          aniversario_esposa: "",
          endereco: "",
          bairro: "",
          funcao_no_ecc: "",
        });
      } else {
        setSucesso("âŒ Erro ao cadastrar casal. Tente novamente.");
      }
      setTimeout(() => setSucesso(""), 4000);
    }
  };

  useEffect(() => {
    loadCasais();
  }, []);

  const labels = {
    nome_esposo: "NOME ESPOSO",
    nome_esposa: "NOME ESPOSA",
    celular_esposo: "CELULAR ESPOSO",
    celular_esposa: "CELULAR ESPOSA",
    aniversario_esposo: "ANIVERSÃRIO ESPOSO ",
    aniversario_esposa: "ANIVERSÃRIO ESPOSA ",
    endereco: "ENDEREÃ‡O",
    bairro: "BAIRRO",
    funcao_no_ecc: "FUNÃ‡ÃƒO NO ECC",
  };

  // -------------------
  // 3ï¸âƒ£ Componente de AtualizaÃ§Ã£o
  // -------------------
  function AtualizaCasal({ casalId, voltar }) {
    const [formAtualiza, setFormAtualiza] = useState({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });

    useEffect(() => {
      axios
        .get(`http://localhost:8000/casais`)
        .then((res) => {
          const casal = res.data.find((c) => c.id === casalId);
          if (casal) setFormAtualiza(casal);
        })
        .catch((err) => console.log(err));
    }, [casalId]);

    const handleChange = (e) => {
      setFormAtualiza({ ...formAtualiza, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      axios
        .put(`http://localhost:8000/casais/${casalId}`, formAtualiza)
        .then(() => {
          alert("Casal atualizado com sucesso!");
          voltar();
          loadCasais();
        })
        .catch((err) => console.log(err));
    };

    return (
      <div>
        <h2>Atualizar Casal</h2>
