import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  // Carrega todos os casais
  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  // Carrega aniversariantes
  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  // Cadastra casal
  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8000/casais", form);
    setForm({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });
    loadCasais();
    setSucesso("âœ… Casal cadastrado com sucesso!");
    setTimeout(() => setSucesso(""), 3000);
  };

  useEffect(() => {
    loadCasais();
  }, []);

  // Formata data YYYY-MM-DD para DD/MM/YYYY
  const formatarData = (dataStr) => {
    if (!dataStr) return "";
    const [ano, mes, dia] = dataStr.split("-");
    return `${dia}/${mes}/${ano}`;
  };

  return (
    <div style={styles.container}>
      <h1>Cadastro de Casais</h1>

      <div style={styles.buttonContainer}>
        <button onClick={() => setTelaAtual("cadastro")}>
          Cadastrar Casal
        </button>
        <button onClick={() => setTelaAtual("lista")}>Buscar Casal</button>
        <button
          onClick={() => {
            setTelaAtual("aniversariantes");
            loadAniversariantes();
          }}
        >
          Aniversariantes
        </button>
      </div>

      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit} style={styles.form}>
          {Object.keys(form).map((field) => (
            <div key={field} style={styles.formGroup}>
              <label>{field.replace(/_/g, " ")}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">Salvar Casal</button>
          {sucesso && <p style={styles.sucesso}>{sucesso}</p>}
        </form>
      )}

      {telaAtual === "lista" && (
        <>
          <h2>Casais Cadastrados</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id}>
                <strong>{c.nome_esposo}</strong> (
                {formatarData(c.aniversario_esposo)}) &nbsp;|&nbsp;
                <strong>{c.nome_esposa}</strong> (
                {formatarData(c.aniversario_esposa)}) &nbsp;|&nbsp;
                <strong>Bairro:</strong> {c.bairro}
              </li>
            ))}
          </ul>
        </>
      )}

      {telaAtual === "aniversariantes" && (
        <>
          <h2>ðŸŽ‰ ParabÃ©ns! ðŸŽ‚</h2>
          {aniversariantes.length > 0 ? (
            aniversariantes.map((pessoa) => (
              <div key={pessoa.id} style={styles.aniversarianteCard}>
                ðŸŽ‰ <strong>{pessoa.nome}</strong> faz aniversÃ¡rio hoje (
                {pessoa.aniversario})!
                <p>
                  ðŸ“– "O amor Ã© paciente, o amor Ã© bondoso..." (1 CorÃ­ntios 13:4)
                </p>
              </div>
            ))
          ) : (
            <p>Hoje nÃ£o hÃ¡ aniversariantes.</p>
          )}
        </>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "600px",
    margin: "20px auto",
    padding: "20px",
    background: "#f9f9f9",
    borderRadius: "10px",
    textAlign: "center",
  },
  buttonContainer: {
    display: "flex",
    justifyContent: "space-around",
    marginBottom: "20px",
  },
  form: {
    textAlign: "left",
  },
  formGroup: {
    marginBottom: "10px",
  },
  sucesso: {
    color: "green",
  },
  aniversarianteCard: {
    padding: "10px",
    margin: "10px 0",
    border: "1px solid #ddd",
    borderRadius: "8px",
    background: "#fff",
  },
};

export default App;
