// ----------------------------
// IMPORTAÇÕES
// ----------------------------
import React, { useEffect, useState } from "react"; // React e hooks
import axios from "axios"; // Para fazer requisições HTTP
import "./App.css"; // Estilos da aplicação

// ----------------------------
// COMPONENTE PRINCIPAL
// ----------------------------
function App() {
  // ----------------------------
  // ESTADOS PRINCIPAIS
  // ----------------------------
  const [casais, setCasais] = useState([]); // Lista de casais cadastrados
  const [telaAtual, setTelaAtual] = useState("menu"); // Controle de tela atual (menu, cadastro, lista, aniversariantes, relatorios)
  const [sucesso, setSucesso] = useState(""); // Mensagens de sucesso/erro
  const [aniversariantes, setAniversariantes] = useState([]); // Lista de aniversariantes do dia

  // Formulário de cadastro de casal
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });

  // ----------------------------
  // FUNÇÃO PARA CARREGAR CASAIS
  // ----------------------------
  const loadCasais = async () => {
    try {
      const res = await axios.get("http://localhost:8000/casais"); // GET do backend
      setCasais(res.data); // Atualiza lista
    } catch (err) {
      console.error("Erro ao carregar casais:", err);
    }
  };

  // ----------------------------
  // FUNÇÃO PARA CARREGAR ANIVERSARIANTES
  // ----------------------------
  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  // ----------------------------
  // USEEFFECT - CARREGA CASAIS QUANDO O COMPONENTE MONTA
  // ----------------------------
  useEffect(() => {
    loadCasais();
  }, []);

  // ----------------------------
  // FUNÇÃO DE CADASTRO DE CASAL
  // ----------------------------
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form); // POST para cadastrar

      // Limpa formulário após cadastro
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });

      loadCasais(); // Atualiza lista de casais
      setSucesso("✅ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("⚠️ Este casal já está cadastrado!");
      } else {
        setSucesso("❌ Erro ao cadastrar casal. Tente novamente.");
      }
      setTimeout(() => setSucesso(""), 4000);
    }
  };

  // ----------------------------
  // COMPONENTE INTERNO: ATUALIZAÇÃO DE CASAIS
  // ----------------------------
  function AtualizaCasal({ casalId, voltar }) {
    // Estado do formulário de atualização
    const [formAtualiza, setFormAtualiza] = useState({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      endereco: "",
      bairro: "",
      funcao: "",
      aniversario: "",
    });

    // Carrega dados do casal selecionado
    useEffect(() => {
      axios
        .get(`http://localhost:8000/casais/${casalId}`)
        .then((res) => setFormAtualiza(res.data))
        .catch((err) => console.log(err));
    }, [casalId]);

    // Atualiza estado quando usuário digita
    const handleChange = (e) => {
      setFormAtualiza({ ...formAtualiza, [e.target.name]: e.target.value });
    };

    // Salva alterações
    const handleSubmitAtualiza = (e) => {
      e.preventDefault();
      axios
        .put(`http://localhost:8000/casais/${casalId}`, formAtualiza) // PUT no backend
        .then(() => {
          alert("Casal atualizado com sucesso!");
          loadCasais(); // Atualiza lista
          voltar(); // Volta para tela anterior
        })
        .catch((err) => console.log(err));
    };

    return (
      <div className="atualiza-container">
        <h2>Atualizar Casal</h2>
        <form onSubmit={handleSubmitAtualiza}>
          <input
            name="nome_esposo"
            value={formAtualiza.nome_esposo}
            onChange={handleChange}
            placeholder="Nome do Esposo"
          />
          <input
            name="nome_esposa"
            value={formAtualiza.nome_esposa}
            onChange={handleChange}
            placeholder="Nome da Esposa"
          />
          <input
            name="celular_esposo"
            value={formAtualiza.celular_esposo}
            onChange={handleChange}
            placeholder="Celular do Esposo"
          />
          <input
            name="celular_esposa"
            value={formAtualiza.celular_esposa}
            onChange={handleChange}
            placeholder="Celular da Esposa"
          />
          <input
            name="endereco"
            value={formAtualiza.endereco}
            onChange={handleChange}
            placeholder="Endereço"
          />
          <input
            name="bairro"
            value={formAtualiza.bairro}
            onChange={handleChange}
            placeholder="Bairro"
          />
          <input
            name="funcao"
            value={formAtualiza.funcao}
            onChange={handleChange}
            placeholder="Função já exercida"
          />
          <input
            type="date"
            name="aniversario"
            value={formAtualiza.aniversario}
            onChange={handleChange}
          />
          <button type="submit">Atualizar</button>
        </form>
        <button onClick={voltar}>⬅ Voltar</button>
      </div>
    );
  }

  // ----------------------------
  // LABELS PARA FORMULÁRIO
  // ----------------------------
  const labels = {
    nome_esposo: "NOME ESPOSO",
    nome_esposa: "NOME ESPOSA",
    celular_esposo: "CELULAR ESPOSO",
    celular_esposa: "CELULAR ESPOSA",
    aniversario_esposo: "ANIVERSÁRIO ESPOSO",
    aniversario_esposa: "ANIVERSÁRIO ESPOSA",
    endereco: "ENDEREÇO",
    bairro: "BAIRRO",
    funcao_no_ecc: "FUNÇÃO NO ECC",
  };

  // ----------------------------
  // RENDERIZAÇÃO PRINCIPAL
  // ----------------------------
  return (
    <div className="container">
      <h1>CADASTRO DE CASAIS</h1>

      {/* MENU PRINCIPAL */}
      {telaAtual === "menu" && (
        <div className="button-container">
          <button onClick={() => setTelaAtual("cadastro")}>
            CADASTRAR CASAL
          </button>
          <button onClick={() => setTelaAtual("lista")}>BUSCAR CASAL</button>
          <button
            onClick={() => {
              setTelaAtual("aniversariantes");
              loadAniversariantes();
            }}
          >
            ANIVERSARIANTES DO DIA
          </button>
          <button onClick={() => setTelaAtual("relatorios")}>EXCLUIR</button>
        </div>
      )}

      {/* BOTÃO DE VOLTAR */}
      {telaAtual !== "menu" && telaAtual !== "atualiza" && (
        <button onClick={() => setTelaAtual("menu")}>⬅ VOLTAR</button>
      )}

      {/* FORMULÁRIO DE CADASTRO */}
      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{labels[field]}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">SALVAR CASAL</button>
          {sucesso && <p className="msg-sucesso">{sucesso}</p>}
        </form>
      )}

      {/* LISTA DE CASAIS */}
      {telaAtual === "lista" && (
        <>
          <h2>CASAIS CADASTRADOS</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa}
                </p>
                <p>
                  <strong>Endereço:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>Função no ECC:</strong> {c.funcao_no_ecc}
                </p>

                {/* BOTÃO DE ATUALIZAR */}
                <button
                  onClick={() => setTelaAtual({ tela: "atualiza", id: c.id })}
                  className="btn-atualizar"
                >
                  Atualizar
                </button>
              </li>
            ))}
          </ul>
        </>
      )}

      {/* TELA DE ATUALIZAÇÃO */}
      {telaAtual.tela === "atualiza" && (
        <AtualizaCasal
          casalId={telaAtual.id}
          voltar={() => setTelaAtual("lista")}
        />
      )}

      {/* ANIVERSARIANTES */}
      {telaAtual === "aniversariantes" && (
        <div className="aniversariantes-container">
          <h2>🎉 PARABÉNS! 🎂</h2>
          {aniversariantes.length > 0 ? (
            aniversariantes.map((p) => (
              <div key={p.id} className="aniversariante-card">
                <p>
                  🎂 <strong>{p.nome}</strong> faz aniversário hoje!
                </p>
                <p className="versiculo">
                  📖 "O amor é paciente, o amor é bondoso..." <br /> (1
                  Coríntios 13:4)
                </p>
              </div>
            ))
          ) : (
            <p>Hoje não há aniversariantes.</p>
          )}
        </div>
      )}

      {/* EXCLUSÃO */}
      {telaAtual === "relatorios" && (
        <>
          <h2>EXCLUIR CASAL</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal excluído com sucesso!");
                    }
                  }}
                  className="btn-excluir"
                >
                  EXCLUIR
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
