import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [casaisFiltrados, setCasaisFiltrados] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios, atualiza
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);
  const [busca, setBusca] = useState("");

  // ------------------- Fun√ß√µes de API -------------------
  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
    setCasaisFiltrados(res.data);
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form);
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });
      loadCasais();
      setSucesso("‚úÖ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("‚ö†Ô∏è Este casal j√° est√° cadastrado!");
      } else {
        setSucesso("‚ùå Erro ao cadastrar casal. Tente novamente.");
      }
      setTimeout(() => setSucesso(""), 4000);
    }
  };

  // ------------------- Filtrar casais -------------------
  const filtrarCasais = () => {
    if (!busca) {
      setCasaisFiltrados(casais);
      return;
    }
    const termo = busca.toLowerCase();
    const filtrados = casais.filter(
      (c) =>
        c.nome_esposo.toLowerCase().includes(termo) ||
        c.nome_esposa.toLowerCase().includes(termo) ||
        c.id.toString() === termo
    );
    setCasaisFiltrados(filtrados);
  };

  // ------------------- Componente Atualiza Casal -------------------
  function AtualizaCasal({ casalId, voltar }) {
    const [form, setForm] = useState({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
    });

    useEffect(() => {
      axios
        .get(`http://localhost:8000/casais/${casalId}`)
        .then((res) => setForm(res.data))
        .catch((err) => console.log(err));
    }, [casalId]);

    const handleChange = (e) => {
      setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      axios
        .put(`http://localhost:8000/casais/${casalId}`, form)
        .then(() => {
          alert("Casal atualizado com sucesso!");
          voltar();
          loadCasais();
        })
        .catch((err) => console.log(err));
    };

    return (
      <div>
        <h2>Atualizar Casal</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={form.id || casalId}
            disabled
            placeholder="ID do casal"
          />
          <input
            name="nome_esposo"
            value={form.nome_esposo}
            onChange={handleChange}
            placeholder="Nome do Esposo"
          />
          <input
            type="date"
            name="aniversario_esposo"
            value={form.aniversario_esposo}
            onChange={handleChange}
          />
          <input
            name="nome_esposa"
            value={form.nome_esposa}
            onChange={handleChange}
            placeholder="Nome da Esposa"
          />
          <input
            type="date"
            name="aniversario_esposa"
            value={form.aniversario_esposa}
            onChange={handleChange}
          />
          <input
            name="celular_esposo"
            value={form.celular_esposo}
            onChange={handleChange}
            placeholder="Celular do Esposo"
          />
          <input
            name="celular_esposa"
            value={form.celular_esposa}
            onChange={handleChange}
            placeholder="Celular da Esposa"
          />
          <input
            name="endereco"
            value={form.endereco}
            onChange={handleChange}
            placeholder="Endere√ßo"
          />
          <input
            name="bairro"
            value={form.bairro}
            onChange={handleChange}
            placeholder="Bairro"
          />
          <input
            name="funcao_no_ecc"
            value={form.funcao_no_ecc}
            onChange={handleChange}
            placeholder="Fun√ß√£o j√° exercida"
          />
          <button type="submit">Atualizar</button>
        </form>
        <button onClick={voltar}>Voltar</button>
      </div>
    );
  }

  useEffect(() => {
    loadCasais();
  }, []);

  const labels = {
    nome_esposo: "NOME ESPOSO",
    nome_esposa: "NOME ESPOSA",
    celular_esposo: "CELULAR ESPOSO",
    celular_esposa: "CELULAR ESPOSA",
    aniversario_esposo: "ANIVERS√ÅRIO ESPOSO ",
    aniversario_esposa: "ANIVERS√ÅRIO ESPOSA ",
    endereco: "ENDERE√áO",
    bairro: "BAIRRO",
    funcao_no_ecc: "FUN√á√ÉO NO ECC",
  };

  return (
    <div className="container">
      <h1>CADASTRO DE CASAIS</h1>

      {/* Menu */}
      <div className="button-container">
        {telaAtual === "menu" ? (
          <>
            <button onClick={() => setTelaAtual("cadastro")}>
              CADASTRAR CASAL
            </button>
            <button onClick={() => setTelaAtual({ tela: "lista" })}>
              BUSCAR CASAL
            </button>
            <button
              onClick={() => {
                setTelaAtual("aniversariantes");
                loadAniversariantes();
              }}
            >
              ANIVERSARIANTES DO DIA
            </button>
            <button onClick={() => setTelaAtual("relatorios")}>EXCLUIR</button>
          </>
        ) : (
          <button onClick={() => setTelaAtual("menu")}>‚¨Ö VOLTAR</button>
        )}
      </div>

      {/* Cadastro */}
      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{labels[field]}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">SALVAR CASAL</button>
          {sucesso && <p className="msg-sucesso">{sucesso}</p>}
        </form>
      )}

      {/* Lista / Busca */}
      {telaAtual.tela === "lista" && (
        <>
          <h2>CASAIS CADASTRADOS</h2>

          {/* Campo de busca */}
          <div style={{ marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Buscar por nome ou ID"
              value={busca}
              onChange={(e) => setBusca(e.target.value)}
              style={{ padding: "5px 10px", marginRight: "10px" }}
            />
            <button
              onClick={filtrarCasais}
              style={{ padding: "5px 15px", marginRight: "10px" }}
            >
              Buscar
            </button>
            <button
              onClick={() => {
                setBusca("");
                loadCasais();
              }}
              style={{ padding: "5px 15px" }}
            >
              Listar Todos os Casais
            </button>
          </div>

          {/* Lista filtrada */}
          <ul>
            {casaisFiltrados.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>ID:</strong> {c.id}
                </p>
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa}
                </p>
                <p>
                  <strong>Endere√ßo:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>Fun√ß√£o no ECC:</strong> {c.funcao_no_ecc}
                </p>
                <button
                  onClick={() => setTelaAtual({ tela: "atualiza", id: c.id })}
                >
                  Atualizar
                </button>
              </li>
            ))}
          </ul>
        </>
      )}

      {/* Atualizar */}
      {telaAtual.tela === "atualiza" && (
        <AtualizaCasal
          casalId={telaAtual.id}
          voltar={() => setTelaAtual({ tela: "lista" })}
        />
      )}

      {/* Aniversariantes */}
      {telaAtual === "aniversariantes" && (
        <div className="aniversariantes-container">
          <h2 className="titulo-aniversario">üéâ PARAB√âNS! üéÇ</h2>

          {aniversariantes.length > 0 ? (
            aniversariantes.map((pessoa) => (
              <div key={pessoa.id} className="aniversariante-card">
                <p>
                  üéÇ <strong>{pessoa.nome}</strong> faz anivers√°rio hoje!
                </p>
                <p className="versiculo">
                  üìñ "O amor √© paciente, o amor √© bondoso..." (1 Cor√≠ntios 13:4)
                </p>
                <button
                  className="btn-whatsapp-round"
                  onClick={() => {
                    const mensagem = `üéâ Parab√©ns ${pessoa.nome}! Hoje √© seu anivers√°rio üéÇ\nüìñ O amor √© paciente, o amor √© bondoso... (1 Cor√≠ntios 13:4)`;
                    window.open(
                      `https://wa.me/?text=${encodeURIComponent(mensagem)}`,
                      "_blank"
                    );
                  }}
                >
                  <img
                    src="https://upload.wikimedia.org/wikipedia/commons/6/6b/WhatsApp.svg"
                    alt="WhatsApp"
                  />
                </button>
              </div>
            ))
          ) : (
            <p>Hoje n√£o h√° aniversariantes.</p>
          )}
        </div>
      )}

      {/* Exclus√£o */}
      {telaAtual.tela === "relatorios" && (
        <>
          <h2>EXCLUIR CASAL</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal exclu√≠do com sucesso!");
                    }
                  }}
                  className="btn-excluir"
                >
                  EXCLUIR
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
