import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios

  // Estado form separado
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });

  // Estado para mensagem de sucesso separado
  const [sucesso, setSucesso] = useState("");

  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8000/casais", form);
    setForm({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });
    loadCasais();
    setSucesso("âœ… Casal cadastrado com sucesso!");
    setTimeout(() => setSucesso(""), 3000);
  };

  useEffect(() => {
    loadCasais();
  }, []);

  return (
    <div className="container">
      <h1>Cadastro de Casais</h1>

      {/* Menu de botÃµes */}
      <div className="button-container">
        <button onClick={() => setTelaAtual("cadastro")}>
          Cadastrar Casal
        </button>
        <button onClick={() => setTelaAtual("lista")}>Buscar Casal</button>
        <button onClick={() => setTelaAtual("aniversariantes")}>
          Aniversariantes
        </button>
        <button onClick={() => setTelaAtual("relatorios")}>RelatÃ³rios</button>
      </div>

      {/* RenderizaÃ§Ã£o condicional das telas */}
      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{field.replace(/_/g, " ")}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}

          <button type="submit">Salvar Casal</button>
          {/* Mensagem de sucesso */}
          {sucesso && <p className="msg-sucesso">{sucesso}</p>}
        </form>
      )}

      {telaAtual === "lista" && (
        <>
          <h2>Casais Cadastrados</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo} |{" "}
                  <strong>AniversÃ¡rio:</strong> {c.aniversario_esposo}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa} |{" "}
                  <strong>AniversÃ¡rio:</strong> {c.aniversario_esposa}
                </p>
                <p>
                  <strong>EndereÃ§o:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>FunÃ§Ã£o no ECC:</strong> {c.funcao_no_ecc}
                </p>
              </li>
            ))}
          </ul>
        </>
      )}

      {telaAtual === "aniversariantes" && (
        <h2>Aqui vai a lista de aniversariantes</h2>
      )}

      {telaAtual === "relatorios" && (
        <>
          <h2>Excluir Casal</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal excluÃ­do com sucesso!");
                    }
                  }}
                  className="btn-excluir"
                >
                  Excluir
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
