import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu");
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  // Carregar todos os casais
  const loadCasais = async () => {
    try {
      const res = await axios.get("http://localhost:8000/casais");
      setCasais(res.data);
    } catch (err) {
      console.log("Erro ao carregar casais:", err);
    }
  };

  // Carregar aniversariantes do dia
  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  // Cadastro de casal
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form);
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });
      loadCasais();
      setSucesso("✅ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("⚠️ Este casal já está cadastrado!");
      } else {
        setSucesso("❌ Erro ao cadastrar casal. Tente novamente.");
      }
      setTimeout(() => setSucesso(""), 4000);
    }
  };

  // -------------------
  // Componente AtualizaCasal
  // -------------------
  function AtualizaCasal({ casalId, voltar }) {
    const [form, setForm] = useState({});
    const [casalOriginal, setCasalOriginal] = useState({});

    useEffect(() => {
      if (!casalId) return;
      axios
        .get(`http://localhost:8000/casais/${casalId}`)
        .then((res) => {
          setForm(res.data);
          setCasalOriginal(res.data); // guardar dados originais
        })
        .catch((err) => console.log(err));
    }, [casalId]);

    const handleChange = (e) => {
      setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmitAtualiza = (e) => {
      e.preventDefault();

      const dadosAtualizados = {};
      Object.keys(form).forEach((key) => {
        if (form[key] !== casalOriginal[key]) {
          dadosAtualizados[key] = form[key];
        }
      });

      if (Object.keys(dadosAtualizados).length === 0) {
        alert("Nenhum campo foi alterado!");
        return;
      }

      axios
        .patch(`http://localhost:8000/casais/${casalId}`, dadosAtualizados)
        .then(() => {
          alert("Casal atualizado com sucesso!");
          voltar();
          loadCasais();
        })
        .catch((err) => console.log(err));
    };

    return (
      <div>
        <h2>Atualizar Casal</h2>
        <form onSubmit={handleSubmitAtualiza}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{field.replace("_", " ").toUpperCase()}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                name={field}
                value={form[field] || ""}
                onChange={handleChange}
              />
            </div>
          ))}
          <button type="submit">Atualizar</button>
        </form>
        <button onClick={voltar}>Voltar</button>
      </div>
    );
  }

  useEffect(() => {
    loadCasais();
  }, []);

  const labels = {
    nome_esposo: "NOME ESPOSO",
    nome_esposa: "NOME ESPOSA",
    celular_esposo: "CELULAR ESPOSO",
    celular_esposa: "CELULAR ESPOSA",
    aniversario_esposo: "ANIVERSÁRIO ESPOSO",
    aniversario_esposa: "ANIVERSÁRIO ESPOSA",
    endereco: "ENDEREÇO",
    bairro: "BAIRRO",
    funcao_no_ecc: "FUNÇÃO NO ECC",
  };

  return (
    <div className="container">
      <h1>CADASTRO DE CASAIS</h1>

      {/* Menu */}
      <div className="button-container">
        {telaAtual === "menu" ? (
          <>
            <button onClick={() => setTelaAtual("cadastro")}>
              CADASTRAR CASAL
            </button>
            <button onClick={() => setTelaAtual("lista")}>BUSCAR CASAL</button>
            <button
              onClick={() => {
                setTelaAtual("aniversariantes");
                loadAniversariantes();
              }}
            >
              ANIVERSARIANTES DO DIA
            </button>
            <button onClick={() => setTelaAtual("relatorios")}>EXCLUIR</button>
          </>
        ) : (
          <button onClick={() => setTelaAtual("menu")}>⬅ VOLTAR</button>
        )}
      </div>

      {/* Cadastro */}
      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{labels[field]}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">SALVAR CASAL</button>
          {sucesso && <p className="msg-sucesso">{sucesso}</p>}
        </form>
      )}

      {/* Lista */}
      {telaAtual === "lista" && (
        <>
          <h2>CASAIS CADASTRADOS</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa}
                </p>
                <p>
                  <strong>Endereço:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>Função no ECC:</strong> {c.funcao_no_ecc}
                </p>

                <button
                  onClick={() =>
                    setTelaAtual({ tela: "atualiza", casalId: c.id })
                  }
                >
                  Atualizar
                </button>
              </li>
            ))}
          </ul>
        </>
      )}

      {/* Atualizar */}
      {telaAtual.tela === "atualiza" && (
        <AtualizaCasal
          casalId={telaAtual.casalId}
          voltar={() => setTelaAtual("lista")}
        />
      )}

      {/* Aniversariantes */}
      {telaAtual === "aniversariantes" && (
        <div className="aniversariantes-container">
          <h2 className="titulo-aniversario">🎉 PARABÉNS! 🎂</h2>
          {/* balões e confetes */}
        </div>
      )}

      {/* Exclusão */}
      {telaAtual === "relatorios" && (
        <>
          <h2>EXCLUIR CASAL</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal excluído com sucesso!");
                    }
                  }}
                  className="btn-excluir"
                >
                  EXCLUIR
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
