import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  // -------------------
  // 1Ô∏è‚É£ Estados do App
  // -------------------
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios, atualiza
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  // -------------------
  // 2Ô∏è‚É£ Fun√ß√µes de API
  // -------------------
  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form);
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });
      loadCasais();
      setSucesso("‚úÖ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("‚ö†Ô∏è Este casal j√° est√° cadastrado!");
        setForm({
          nome_esposo: "",
          nome_esposa: "",
          celular_esposo: "",
          celular_esposa: "",
          aniversario_esposo: "",
          aniversario_esposa: "",
          endereco: "",
          bairro: "",
          funcao_no_ecc: "",
        });
      } else {
        setSucesso("‚ùå Erro ao cadastrar casal. Tente novamente.");
      }
      setTimeout(() => setSucesso(""), 4000);
    }
  };

  // -------------------
  // 3Ô∏è‚É£ Componente de Atualiza√ß√£o
  // -------------------
  function AtualizaCasal({ casalId, voltar }) {
    const [form, setForm] = useState({
      nome_esposo: "",
