import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios

  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });

  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://localhost:8000/casais", form);

      // limpa formul√°rio no sucesso
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });

      loadCasais();
      setSucesso("‚úÖ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("‚ö†Ô∏è Este casal j√° est√° cadastrado!");

        // üöÄ limpa o formul√°rio tamb√©m em caso de duplicado
        setForm({
          nome_esposo: "",
          nome_esposa: "",
          celular_esposo: "",
