import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });

  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8000/casais", form);
    setForm({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });
    loadCasais();
    setTelaAtual("lista"); // volta para lista após salvar
  };

  useEffect(() => {
    loadCasais();
  }, []);

  return (
    <div className="container">
      <h1>Cadastro de Casais</h1>

      {/* Menu de botões */}
      <div className="button-container">
        <button onClick={() => setTelaAtual("cadastro")}>
          Cadastrar Casal
        </button>
        <button onClick={() => setTelaAtual("lista")}>Buscar Casal</button>
        <button onClick={() => setTelaAtual("aniversariantes")}>
          Aniversariantes
        </button>
        <button onClick={() => setTelaAtual("relatorios")}>Relatórios</button>
      </div>

      {/* Renderização condicional das telas */}
      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{field.replace(/_/g, " ")}:</label>
              <input
                type="text"
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">Salvar Casal</button>
        </form>
      )}

      {telaAtual === "lista" && (
        <>
          <h2>Casais Cadastrados</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id}>
                {c.nome_esposo} e {c.nome_esposa} - {c.bairro}
              </li>
            ))}
          </ul>
        </>
      )}

      {telaAtual === "aniversariantes" && (
        <h2>Aqui vai a lista de aniversariantes</h2>
      )}

      {telaAtual === "relatorios" && <h2>Aqui vai a geração de relatórios</h2>}
    </div>
  );
}

export default App;
