import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios

  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });

  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8000/casais", form);
    setForm({
      Nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });
    loadCasais();
    setSucesso("✅ Casal cadastrado com sucesso!");
    setTimeout(() => setSucesso(""), 3000);
  };

  useEffect(() => {
    loadCasais();
  }, []);

  // Função para formatar data YYYY-MM-DD para DD/MM/YYYY
  function formatarData(dataStr) {
    if (!dataStr) return "";
    const [ano, mes, dia] = dataStr.split("-");
    return `${dia}/${mes}/${ano}`;
  }

  return (
    <div className="container">
      <h1>CADASTRO DE CASAIS</h1>

      <div className="button-container">
        <button onClick={() => setTelaAtual("cadastro")}>
          CADASTRAR CASAL
        </button>
        <button onClick={() => setTelaAtual("lista")}>BUSCAR CASAL</button>
        <button
          onClick={() => {
            setTelaAtual("aniversariantes");
            loadAniversariantes();
          }}
        >
          ANIVERSARIANTES DO DIA
        </button>
        <button onClick={() => setTelaAtual("relatorios")}>EXCLUIR</button>
      </div>

      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{field.replace(/_/g, " ")}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}

          <button type="submit">SALVAR CASAL</button>
          {sucesso && <p className="msg-sucesso">{sucesso}</p>}
        </form>
      )}

      {telaAtual === "lista" && (
        <>
          <h2>CASAIS CADASTRADO</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo} |{" "}
                  <strong>Aniversário:</strong>{" "}
                  {formatarData(c.aniversario_esposo)}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa} |{" "}
                  <strong>Aniversário:</strong>{" "}
                  {formatarData(c.aniversario_esposa)}
                </p>
                <p>
                  <strong>Endereço:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>Função no ECC:</strong> {c.funcao_no_ecc}
                </p>
              </li>
            ))}
          </ul>
        </>
      )}

      {telaAtual === "aniversariantes" && (
        <>
          <h2>🎉 Parabéns! 🎂</h2>
          {aniversariantes.length > 0 ? (
            aniversariantes.map((pessoa) => (
              <div key={pessoa.id} className="aniversariante-card">
                <p>
                  🎉 <strong>{pessoa.nome}</strong> faz aniversário hoje (
                  {pessoa.aniversario})!
                </p>
                <p>
                  📖 "O amor é paciente, o amor é bondoso..." (1 Coríntios 13:4)
                </p>
              </div>
            ))
          ) : (
            <p>Hoje não há aniversariantes.</p>
          )}
        </>
      )}

      {telaAtual === "relatorios" && (
        <>
          <h2>Excluir Casal</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal excluído com sucesso!");
                    }
                  }}
                  className="btn-excluir"
                >
                  Excluir
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
