import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu");
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  const loadCasais = async () => {
    try {
      const res = await axios.get("http://localhost:8000/casais");
      setCasais(res.data);
    } catch (err) {
      console.log("Erro ao carregar casais:", err);
    }
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (err) {
      console.log(err);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form);
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });
      loadCasais();
      setSucesso("✅ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      setSucesso("❌ Erro ao cadastrar casal");
      setTimeout(() => setSucesso(""), 3000);
    }
  };

  function AtualizaCasal({ casalId, voltar }) {
    const [formAtualiza, setFormAtualiza] = useState({});
    const [casalOriginal, setCasalOriginal] = useState({});

    useEffect(() => {
      if (!casalId) return;
      axios
        .get(`http://localhost:8000/casais/${casalId}`)
        .then((res) => {
          setFormAtualiza(res.data);
          setCasalOriginal(res.data);
        })
        .catch((err) => console.log(err));
    }, [casalId]);

    const handleChange = (e) => {
      setFormAtualiza({ ...formAtualiza, [e.target.name]: e.target.value });
    };

    const handleSubmitAtualiza = (e) => {
      e.preventDefault();

      const dadosAtualizados = {};
      Object.keys(formAtualiza).forEach((key) => {
        if (formAtualiza[key] !== casalOriginal[key]) {
          dadosAtualizados[key] = formAtualiza[key];
        }
      });

      if (Object.keys(dadosAtualizados).length === 0) {
        alert("Nenhum campo foi alterado!");
        return;
      }

      axios
        .patch(`http://localhost:8000/casais/${casalId}`, dadosAtualizados)
        .then(() => {
          alert("Casal atualizado com sucesso!");
          voltar();
          loadCasais();
        })
        .catch((err) => console.log(err));
    };

    return (
      <div>
        <h2>Atualizar Casal</h2>
        {Object.keys(formAtualiza).length > 0 && (
          <form onSubmit={handleSubmitAtualiza}>
            {Object.keys(formAtualiza).map((field) => (
              <div key={field}>
                <label>{field.replace("_", " ").toUpperCase()}:</label>
                <input
                  type={field.includes("aniversario") ? "date" : "text"}
                  name={field}
                  value={formAtualiza[field] || ""}
                  onChange={handleChange}
                />
              </div>
            ))}
            <button type="submit">Atualizar</button>
          </form>
        )}
        <button onClick={voltar}>Voltar</button>
      </div>
    );
  }

  useEffect(() => {
    loadCasais();
  }, []);

  return (
    <div className="container">
      <h1>CADASTRO DE CASAIS</h1>

      {/* Menu */}
      {telaAtual === "menu" ? (
        <div className="button-container">
          <button onClick={() => setTelaAtual("cadastro")}>
            CADASTRAR CASAL
          </button>
          <button onClick={() => setTelaAtual("lista")}>BUSCAR CASAL</button>
          <button
            onClick={() => {
              setTelaAtual("aniversariantes");
              loadAniversariantes();
            }}
          >
            ANIVERSARIANTES
          </button>
          <button onClick={() => setTelaAtual("relatorios")}>EXCLUIR</button>
        </div>
      ) : (
        <button onClick={() => setTelaAtual("menu")}>⬅ VOLTAR</button>
      )}

      {/* Cadastro */}
      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{field.replace("_", " ").toUpperCase()}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">SALVAR CASAL</button>
          {sucesso && <p>{sucesso}</p>}
        </form>
      )}

      {/* Lista */}
      {telaAtual === "lista" && (
        <ul>
          {casais.map((c) => (
            <li key={c.id}>
              <p>
                {c.nome_esposo} e {c.nome_esposa}
              </p>
              <button
                onClick={() =>
                  setTelaAtual({ tela: "atualiza", casalId: c.id })
                }
              >
                Atualizar
              </button>
            </li>
          ))}
        </ul>
      )}

      {/* Atualizar */}
      {telaAtual.tela === "atualiza" && (
        <AtualizaCasal
          casalId={telaAtual.casalId}
          voltar={() => setTelaAtual("lista")}
        />
      )}
    </div>
  );
}

export default App;
