import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  // -------------------
  // 1Ô∏è‚É£ Estados do App
  // -------------------
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState({ tela: "menu", casalId: null });
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });
  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  // -------------------
  // 2Ô∏è‚É£ Fun√ß√µes de API
  // -------------------
  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/casais", form);
      setForm({
        nome_esposo: "",
        nome_esposa: "",
        celular_esposo: "",
        celular_esposa: "",
        aniversario_esposo: "",
        aniversario_esposa: "",
        endereco: "",
        bairro: "",
        funcao_no_ecc: "",
      });
      loadCasais();
      setSucesso("‚úÖ Casal cadastrado com sucesso!");
      setTimeout(() => setSucesso(""), 3000);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setSucesso("‚ö†Ô∏è Este casal j√° est√° cadastrado!");
      } else {
        setSucesso("‚ùå Erro ao cadastrar casal. Tente novamente.");
      }
      setTimeout(() => setSucesso(""), 4000);
    }
  };

  // -------------------
  // 3Ô∏è‚É£ Componente de Atualiza√ß√£o
  // -------------------
  function AtualizaCasal({ casalId, voltar }) {
    const [form, setForm] = useState({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });

    const [casaisLista, setCasaisLista] = useState([]);

    useEffect(() => {
      if (casalId) {
        axios
          .get(`http://localhost:8000/casais/${casalId}`)
          .then((res) => setForm(res.data))
          .catch((err) => console.log(err));
      } else {
        axios
          .get("http://localhost:8000/casais")
          .then((res) => setCasaisLista(res.data))
          .catch((err) => console.log(err));
      }
    }, [casalId]);

    const handleChange = (e) =>
      setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = (e) => {
      e.preventDefault();
      axios
        .put(`http://localhost:8000/casais/${casalId}`, form)
        .then(() => {
          alert("Casal atualizado com sucesso!");
          voltar();
        })
        .catch((err) => console.log(err));
    };

    if (!casalId) {
      return (
        <div>
          <h2>Escolha o casal para atualizar</h2>
          <ul>
            {casaisLista.map((c) => (
              <li key={c.id}>
                {c.nome_esposo} & {c.nome_esposa}{" "}
                <button
                  onClick={() =>
                    setTelaAtual({ tela: "atualiza", casalId: c.id })
                  }
                >
                  Selecionar
                </button>
              </li>
            ))}
          </ul>
          <button onClick={voltar}>Voltar</button>
        </div>
      );
    }

    return (
      <div>
        <h2>Atualizar Casal</h2>
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{field.replace("_", " ").toUpperCase()}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                name={field}
                value={form[field]}
                onChange={handleChange}
                required
              />
            </div>
          ))}
          <button type="submit">Atualizar</button>
        </form>
        <button onClick={voltar}>Voltar</button>
      </div>
    );
  }

  useEffect(() => {
    loadCasais();
  }, []);

  const labels = {
    nome_esposo: "NOME ESPOSO",
    nome_esposa: "NOME ESPOSA",
    celular_esposo: "CELULAR ESPOSO",
    celular_esposa: "CELULAR ESPOSA",
    aniversario_esposo: "ANIVERS√ÅRIO ESPOSO ",
    aniversario_esposa: "ANIVERS√ÅRIO ESPOSA ",
    endereco: "ENDERE√áO",
    bairro: "BAIRRO",
    funcao_no_ecc: "FUN√á√ÉO NO ECC",
  };

  return (
    <div className="container">
      <h1>CADASTRO DE CASAIS</h1>

      {/* Menu */}
      <div className="button-container">
        {telaAtual.tela === "menu" ? (
          <>
            <button
              onClick={() => setTelaAtual({ tela: "cadastro", casalId: null })}
            >
              CADASTRAR CASAL
            </button>
            <button
              onClick={() => setTelaAtual({ tela: "lista", casalId: null })}
            >
              BUSCAR CASAL
            </button>
            <button
              onClick={() => {
                setTelaAtual({ tela: "aniversariantes", casalId: null });
                loadAniversariantes();
              }}
            >
              ANIVERSARIANTES DO DIA
            </button>
            <button
              onClick={() =>
                setTelaAtual({ tela: "relatorios", casalId: null })
              }
            >
              EXCLUIR
            </button>
            <button
              onClick={() => setTelaAtual({ tela: "atualiza", casalId: null })}
            >
              ATUALIZAR CASAL
            </button>
          </>
        ) : (
          <button onClick={() => setTelaAtual({ tela: "menu", casalId: null })}>
            ‚¨Ö VOLTAR
          </button>
        )}
      </div>

      {/* Cadastro */}
      {telaAtual.tela === "cadastro" && (
        <form onSubmit={handleSubmit}>
          {Object.keys(form).map((field) => (
            <div key={field}>
              <label>{labels[field]}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
              />
            </div>
          ))}
          <button type="submit">SALVAR CASAL</button>
          {sucesso && <p className="msg-sucesso">{sucesso}</p>}
        </form>
      )}

      {/* Lista */}
      {telaAtual.tela === "lista" && (
        <>
          <h2>CASAIS CADASTRADOS</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa}
                </p>
                <p>
                  <strong>Endere√ßo:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>Fun√ß√£o no ECC:</strong> {c.funcao_no_ecc}
                </p>

                {/* Bot√£o atualizar */}
                <button
                  onClick={() =>
                    setTelaAtual({ tela: "atualiza", casalId: c.id })
                  }
                >
                  Atualizar
                </button>
              </li>
            ))}
          </ul>
        </>
      )}

      {/* Atualizar */}
      {telaAtual.tela === "atualiza" && (
        <AtualizaCasal
          casalId={telaAtual.casalId}
          voltar={() => setTelaAtual({ tela: "menu", casalId: null })}
        />
      )}

      {/* Aniversariantes */}
      {telaAtual.tela === "aniversariantes" && (
        <div className="aniversariantes-container">
          <h2 className="titulo-aniversario">üéâ PARAB√âNS! üéÇ</h2>
          <div className="baloes-container">
            {Array.from({ length: 15 }).map((_, i) => (
              <div key={i} className="balao"></div>
            ))}
          </div>
          <div className="confetes-container">
            {Array.from({ length: 20 }).map((_, i) => (
              <div key={i} className="confete"></div>
            ))}
          </div>
          {aniversariantes.length > 0 ? (
            aniversariantes.map((pessoa) => (
              <div key={pessoa.id} className="aniversariante-card">
                <p>
                  üéÇ <strong>{pessoa.nome}</strong> faz anivers√°rio hoje!
                </p>
                <p className="versiculo">
                  üìñ "O amor √© paciente, o amor √© bondoso..." <br /> (1
                  Cor√≠ntios 13:4)
                </p>
                <button
                  className="btn-whatsapp-round"
                  onClick={() => {
                    const mensagem = `üéâ Parab√©ns ${pessoa.nome}! Hoje √© seu anivers√°rio üéÇ\nüìñ O amor √© paciente, o amor √© bondoso... (1 Cor√≠ntios 13:4)`;
                    window.open(
                      `https://wa.me/?text=${encodeURIComponent(mensagem)}`,
                      "_blank"
                    );
                  }}
                >
                  <img
                    src="https://upload.wikimedia.org/wikipedia/commons/6/6b/WhatsApp.svg"
                    alt="WhatsApp"
                  />
                </button>
              </div>
            ))
          ) : (
            <p>Hoje n√£o h√° aniversariantes.</p>
          )}
        </div>
      )}

      {/* Exclus√£o */}
      {telaAtual.tela === "relatorios" && (
        <>
          <h2>EXCLUIR CASAL</h2>
          <ul>
            {casais.map((c) => (
              <li key={c.id} className="casal-card">
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal exclu√≠do com sucesso!");
                    }
                  }}
                  className="btn-excluir"
                >
                  EXCLUIR
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
