import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [casais, setCasais] = useState([]);
  const [telaAtual, setTelaAtual] = useState("menu"); // menu, cadastro, lista, aniversariantes, relatorios

  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });

  const [sucesso, setSucesso] = useState("");
  const [aniversariantes, setAniversariantes] = useState([]);

  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const loadAniversariantes = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aniversariantes");
      setAniversariantes(res.data);
    } catch (error) {
      console.error("Erro ao carregar aniversariantes:", error);
      setAniversariantes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8000/casais", form);
    setForm({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });
    loadCasais();
    setSucesso("✅ Casal cadastrado com sucesso!");
    setTimeout(() => setSucesso(""), 3000);
  };

  useEffect(() => {
    loadCasais();
  }, []);

  // Função para formatar data YYYY-MM-DD para DD/MM/YYYY
  function formatarData(dataStr) {
    if (!dataStr) return "";
    const [ano, mes, dia] = dataStr.split("-");
    return `${dia}/${mes}/${ano}`;
  }

  // Labels em CAIXA ALTA
  const labels = {
    nome_esposo: "NOME ESPOSO",
    nome_esposa: "NOME ESPOSA",
    celular_esposo: "CELULAR ESPOSO",
    celular_esposa: "CELULAR ESPOSA",
    aniversario_esposo: "ANIVERSÁRIO ESPOSO (DD/MM/AAAA)",
    aniversario_esposa: "ANIVERSÁRIO ESPOSA (DD/MM/AAAA)",
    endereco: "ENDEREÇO",
    bairro: "BAIRRO",
    funcao_no_ecc: "FUNÇÃO NO ECC",
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6 text-center">
        CADASTRO DE CASAIS
      </h1>

      <div className="flex justify-center gap-4 mb-6">
        {telaAtual === "menu" ? (
          <>
            <button
              onClick={() => setTelaAtual("cadastro")}
              className="bg-blue-500 text-white px-4 py-2 rounded"
            >
              CADASTRAR CASAL
            </button>
            <button
              onClick={() => setTelaAtual("lista")}
              className="bg-green-500 text-white px-4 py-2 rounded"
            >
              BUSCAR CASAL
            </button>
            <button
              onClick={() => {
                setTelaAtual("aniversariantes");
                loadAniversariantes();
              }}
              className="bg-purple-500 text-white px-4 py-2 rounded"
            >
              ANIVERSARIANTES DO DIA
            </button>
            <button
              onClick={() => setTelaAtual("relatorios")}
              className="bg-red-500 text-white px-4 py-2 rounded"
            >
              EXCLUIR
            </button>
          </>
        ) : (
          <button
            onClick={() => setTelaAtual("menu")}
            className="bg-gray-600 text-white px-4 py-2 rounded"
          >
            ⬅ VOLTAR
          </button>
        )}
      </div>

      {telaAtual === "cadastro" && (
        <form onSubmit={handleSubmit} className="max-w-md mx-auto">
          {Object.keys(form).map((field) => (
            <div key={field} className="mb-4">
              <label className="block mb-1 font-bold">{labels[field]}:</label>
              <input
                type={field.includes("aniversario") ? "date" : "text"}
                value={form[field]}
                onChange={(e) => setForm({ ...form, [field]: e.target.value })}
                required
                className="w-full p-2 border rounded"
              />
            </div>
          ))}

          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded w-full"
          >
            SALVAR CASAL
          </button>
          {sucesso && (
            <p className="text-green-600 font-semibold mt-2">{sucesso}</p>
          )}
        </form>
      )}

      {telaAtual === "lista" && (
        <>
          <h2 className="text-xl font-bold mb-4">CASAIS CADASTRADOS</h2>
          <ul className="space-y-4">
            {casais.map((c) => (
              <li key={c.id} className="p-4 border rounded shadow">
                <p>
                  <strong>Esposo:</strong> {c.nome_esposo} |{" "}
                  <strong>Celular:</strong> {c.celular_esposo} |{" "}
                  <strong>Aniversário:</strong>{" "}
                  {formatarData(c.aniversario_esposo)}
                </p>
                <p>
                  <strong>Esposa:</strong> {c.nome_esposa} |{" "}
                  <strong>Celular:</strong> {c.celular_esposa} |{" "}
                  <strong>Aniversário:</strong>{" "}
                  {formatarData(c.aniversario_esposa)}
                </p>
                <p>
                  <strong>Endereço:</strong> {c.endereco} |{" "}
                  <strong>Bairro:</strong> {c.bairro}
                </p>
                <p>
                  <strong>Função no ECC:</strong> {c.funcao_no_ecc}
                </p>
              </li>
            ))}
          </ul>
        </>
      )}

      {telaAtual === "aniversariantes" && (
        <>
          <h2 className="text-xl font-bold mb-4">🎉 PARABÉNS! 🎂</h2>
          {aniversariantes.length > 0 ? (
            aniversariantes.map((pessoa) => (
              <div
                key={pessoa.id}
                className="p-4 border rounded shadow mb-4 bg-yellow-100"
              >
                <p>
                  🎉 <strong>{pessoa.nome}</strong> faz aniversário hoje (
                  {pessoa.aniversario})!
                </p>
                <p>
                  📖 "O amor é paciente, o amor é bondoso..." (1 Coríntios 13:4)
                </p>
              </div>
            ))
          ) : (
            <p>Hoje não há aniversariantes.</p>
          )}
        </>
      )}

      {telaAtual === "relatorios" && (
        <>
          <h2 className="text-xl font-bold mb-4">EXCLUIR CASAL</h2>
          <ul className="space-y-4">
            {casais.map((c) => (
              <li
                key={c.id}
                className="p-4 border rounded shadow flex justify-between items-center"
              >
                <p>
                  <strong>{c.nome_esposo}</strong> e{" "}
                  <strong>{c.nome_esposa}</strong>
                </p>
                <button
                  onClick={async () => {
                    if (
                      window.confirm(
                        `Deseja realmente excluir o casal ${c.nome_esposo} e ${c.nome_esposa}?`
                      )
                    ) {
                      await axios.delete(
                        `http://localhost:8000/casais/${c.id}`
                      );
                      loadCasais();
                      alert("Casal excluído com sucesso!");
                    }
                  }}
                  className="bg-red-600 text-white px-3 py-1 rounded"
                >
                  EXCLUIR
                </button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
