import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css"; // ðŸ‘ˆ importante!

function App() {
  const [casais, setCasais] = useState([]);
  const [form, setForm] = useState({
    nome_esposo: "",
    nome_esposa: "",
    celular_esposo: "",
    celular_esposa: "",
    aniversario_esposo: "",
    aniversario_esposa: "",
    endereco: "",
    bairro: "",
    funcao_no_ecc: "",
  });

  const loadCasais = async () => {
    const res = await axios.get("http://localhost:8000/casais");
    setCasais(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8000/casais", form);
    setForm({
      nome_esposo: "",
      nome_esposa: "",
      celular_esposo: "",
      celular_esposa: "",
      aniversario_esposo: "",
      aniversario_esposa: "",
      endereco: "",
      bairro: "",
      funcao_no_ecc: "",
    });
    loadCasais();
  };

  useEffect(() => {
    loadCasais();
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>Cadastro de Casais</h1>
      <form onSubmit={handleSubmit}>
        {Object.keys(form).map((field) => (
          <div key={field}>
            <label>{field.replace(/_/g, " ")}:</label>
            <br />
            <input
              type="text"
              value={form[field]}
              onChange={(e) => setForm({ ...form, [field]: e.target.value })}
              required
            />
            <br />
            <br />
          </div>
        ))}
        <button type="submit">Salvar Casal</button>
      </form>

      <h2>Casais Cadastrados</h2>
      <ul>
        {casais.map((c) => (
          <li key={c.id}>
            {c.nome_esposo} e {c.nome_esposa} - {c.bairro}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
